var get_vimeo_videoID = function( url ) {

	var videoID;
	if( url.indexOf( "vimeo.com" ) > 0 ) {
		videoID = url.substr( url.lastIndexOf( "/" ) + 1, url.length );
	} else {
		videoID = url.length > 15 ? null : url;
	}

	return videoID
};


